import React, { Component } from 'react';
import { Form, TextArea, Button, Segment,Icon,Label } from 'semantic-ui-react';
import Managerheader1 from "../Managerpg1/Managerheader1";
import ManagerFooter2 from "../Managerpg2/ManagerFooter2";
import "../Managerpg1/Managermain.css"
import axios from 'axios';
import { Redirect } from "react-router-dom";
import apiconstants from '../employee/config';

var bgColors = {
  "Green": "#ddfada",
  "Yellow": "#d3d3d3",
};


class Managermain4 extends Component {
  constructor() {
    super();
    this.state = {
      dynamic: [],
      disabled:false,
    };
  }

  componentWillMount() {
    let id=this.props.match.params.id
    let month=this.props.match.params.month
    const response = axios({
      method: 'post',
      url: apiconstants.URL + `updatemonthvalue/${id}/${month}`,
    })
      .then(response => {
        console.log(response)

        if (response.data.length != 0) {
          const da = [];
          console.log(JSON.stringify(response.data))
          response.data.map((datas)=>{
            if(datas.type==='d'){
              da.push(datas);
            }
          })
          this.setState({ dynamic: da })}
        else {
          this.setState({ dynamic: this.state.dynamic.concat({ id: this.props.match.params.id, question: "", answer: "", remark: "", month: this.props.match.params.month }) });
        }
      })

  }
  async componentDidMount() {
    console.log("Component Did mount started")
    let val=await this.time();
    console.log("value is "+val)
    if(val===1){
        console.log("it entered the if "+val)
        await setTimeout(()=>{this.setState({disabled:true})},);
    }

}
