import { Icon,Button,Label} from 'semantic-ui-react'
import 'semantic-ui/dist/semantic.min.css';
import MonthYearPicker from 'react-month-year-picker';

class Year extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          // month: 10,
          button:true,
          // year: 2018,
          date: "",
          year:'',
          month:""
        };
      }
        // this.onGoalsClick=this.onGoalsClick.bind(this)
        // this.onFeedbackClick=this.onFeedbackClick.bind(this)



        componentDidMount() {
          this.getDate();

        }

        getDate = () => {
          // // var date = new Date().toDateString();
          // var date = new Date().getMonth()  .toDateString();
          // this.setState({ date });
          let monthNumber = (new Date().getMonth());
          let year = (new Date().getFullYear());

          let monthNames = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
          var month = monthNames[monthNumber];
          //return month;
          this.setState({ month });
          this.setState({ year });
        };

onCalanderClick(){
    return(
    <div>
    <MonthYearPicker 
    style={{width:"10px"}}
      selectedMonth={this.state.month}
      selectedYear={this.state.year}
      minYear={2000}
      maxYear={2030}   
      onChangeYear={year => {
        this.setState({ year: year })
        this.props.store_DateTime
      }}
      //onChangeYear={this.props.store_DateTime}

      onChangeMonth={(month) =>{ 
        let monthNames = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
       // var mon=this.getDate();
        var mon=monthNames[month-1]
        this.setState({ month: mon })
      }}
    />
    <h3>Selected month: {this.state.month}</h3>
    <h3>Selected year: {this.state.year}</h3>
    {/* <input type="month"/> */}



  </div>

    )
}


button=()=>{
    this.setState({button:!this.state.button})
}


