    componentWillMount() {
      const response = axios({
          method: 'patch',
          url: 'http://192.168.1.245:8095/updatemonthvalue',
          data: this.state
      }) 
          .then(response => {
            console.log(response.data)

              if (response.data.length != 0) {

                  console.log(JSON.stringify(response.data[1].ans))
                   this.setState({q:response.data[0].ques})
                   this.setState({a:response.data[0].ans})
                  this.setState({remark:response.data[0].remark})

                }



              else {

                // console.log("second",response)
                  this.setState({q:''});
                  this.setState({a:''});
                  this.setState({remark:''});

                }
          })

      }


    handleQuesChange = i => evt => {
      const newques= this.state.dynamic.map((row, j) => {
        if (i !== j) return row;
        return { ...row, q: evt.target.value};
      });

      this.setState({ dynamic: newques });
    };
    handleAnsChange = i => evt => {
        const newans= this.state.dynamic.map((row, j) => {
          if (i !== j) return row;
          return { ...row, a: evt.target.value};
        });

        this.setState({ dynamic: newans});
      };
      handleRemarkChange = i => evt => {
        const newr= this.state.dynamic.map((row, j) => {
          if (i !== j) return row;
          return { ...row, remark: evt.target.value};
        });

        this.setState({ dynamic: newr});
      };



    handleSubmit = e => {
        e.preventDefault();
        console.log(JSON.stringify(this.state.dynamic));
        const {id,q,a,remark} = this.state;
        // axios

        //     .post('http://192.168.1.245:8095/qanda_dynamic', JSON.stringify(this.state.dynamic))

        //     .then(response => {
        //         console.log(response)
        //         // return response;

        //     })
        //     .then(response => {
        //         console.log(JSON.stringify(response.data))
        //                   })
        //     .catch(error => {
        //         console.log(error);

        //     })
            fetch('http://192.168.1.245:8095/qanda_dynamic',{
                method:'POST',
                headers:{
                    'Content-Type':'application/json'
                },
                body:JSON.stringify(this.state.dynamic)
            }).then(res=>{
                console.log(res)
            }).then(response=>console.log(response))
    };


    handleAdd = () => {
      this.setState({

        dynamic: this.state.dynamic.concat({ id:24, q:"" ,a:"" , remark:"",month:"jan" })
        //dynamic: this.state.dynamic.concat([{ a: "" }]),
      //  dynamic: this.state.dynamic.concat([{remark: "" }])

      });
    };


    render() {
        // const {id}=11
        // const {month}="oct"
        {
          if (this.state.redirect) {
              return <Redirect to="/month/static-form/dynamic-form/goals"/>;

          }
      }

      return (
          <div>
               <Managerheader1/>
               <br />
               <br />
               <br />
               <br />
               <br />

        <form >
          {this.state.dynamic.map((row, i) => (
              <div style={{paddingLeft:"10%"}} className="form1">

         <TextArea  style={{color:"black"}}
                    placeholder="enter your question" 
                    rows={2}
                    cols={150}
                    value={this.state.q}
                    onChange={this. handleQuesChange(i) }
         />
         <br/>

         <TextArea style={{color:"black"}}
                   placeholder='enter answer here'
                   rows={3}
                   cols={150}
                   value={this.state.a}
                   onChange={this. handleAnsChange(i) }
                   />
                   <br />
         <TextArea style={{color:"black"}}
                   placeholder='Remarks. if any?'
                   rows={3}
                   cols={150}
                   value={this.state.remark}
                   onChange={this. handleRemarkChange(i) }

         />

    </div>
          ))}
          <button
            type="button"
            onClick={this.handleAdd}
            className="add-button"
          >
            Add more questions
          </button>
          <br />
          <button onClick={this.handleSubmit} className="save-button" >save</button>



        </form>
        <br />
        <br />
        <br />
        <br />
        <br />
        <br />
        <br />
        <ManagerFooter2/>
        </div>

      );
    }
  }

  export default Managermain4;
