import React,{Component} from 'react';
import './hr.css';
//import logo from './leaps.jpg';
import Header from './Header';
import Footer from './Footer';
import Profile from './Profile';
import login from './login.png';
import Table from './Table';
import ManagerFooter from "../Managerpg1/ManagerFooter";
import {BrowserRouter as Router,Route,Link} from "react-router-dom";
import { Redirect } from 'react-router-dom';
import AboutUs from "./AboutUs";
import DropDown from './DropDown';
import apiconstants from '../employee/config';
import hr from '../images/hr.png'
import '../Managerpg1/Managermain.css'
import { Form, TextArea,Button,Segment,Icon,Label} from 'semantic-ui-react';
// import { Form, TextArea,Button,Segment,Icon,Label,Table,Header } from 'semantic-ui-react';



class HrMainPage extends Component {
    state={
        data:[]
    }
    constructor(props) {
        super(props);

        this.state = {
            data: [],
            data1:'',
        };
    }

    componentDidMount=()=>{
        this.autho();
        this.profile();
    }

    autho() {
        //const {navigate} = this.props.navigation;
        // var url = `http://192.168.1.244:8095/hrviewall`;

        fetch(  apiconstants.URL + `hrviewall`,
        {
            method: 'GET',

            // body: JSON.stringify(this.state),
            headers: {
                'content-Type': 'application/json',
                'Accept': 'application/json',
            },
        }).then(response => response.json())
            .then(res => {

                console.log('res', JSON.stringify(res));
                this.setState({data:res});

            })
    }



    profile() {
        let id = this.props.match.params.id
        console.log(id)
        fetch(apiconstants.URL +`profile/${id}`, {
        method: 'GET',
        // body: JSON.stringify({
        // // mid: id
        // }),
        headers: {
        'content-Type': 'application/json',
        'Accept': 'application/json',
        },
        })
        .then(res => res.json())
        .then(res => {
        console.log(res[0])
        console.log('res', JSON.stringify(res));
        //console.log(JSON.stringify(res.data)),
        this.setState({ data1: res });
        console.log(res)
        })
        }


    render() {
        /*if (this.state.loading) {
            return <div>loading...</div>;
        }

        if (!this.state.person) {
            return <div>didn't get a person</div>;
        }*/

        const {data}=this.state;
        const {data1}=this.state;
        return (

            <div>
                <div>
                    <Header />



                    <div className="profiletab" >
        {/* <Icon name='big user' /> */}
        <img style={{width:"50px"}} src={hr}/>
      &nbsp; &nbsp;
        {data1.empname}<br/>
        {data1.email}

    </div>


                </div>
                <br/>
                <br/>
                <br/>
                <main>

                    <DropDown data={data}/>
                    <br />
                    <br />
                     <br/>

                </main>
                <br />
                <br />
                <br />
                <ManagerFooter />
            </div>



);
}

}


export default HrMainPage;
