import React from 'react'
import Header from '../Components/Header'
import 'semantic-ui/dist/semantic.min.css';
import './Manager.css'

import Sidebar from '../Components/Sidebar'
import Footer from "../Components/Footer";
import { Link } from "react-router-dom";
import manager from "../images/manager1.png"
import apiconstants from '../employee/config';
import { Form, TextArea, Button, Segment, Icon, Label, Table,Dropdown,Placeholder } from 'semantic-ui-react';
import axios from 'axios'

class Managermain extends React.Component {

    constructor(props) {
        super(props);


        this.state = {
            data: [],
            data1: '',
            repo: [],
            empname: '',
            email: '',
            phone: '',
            designation: '',
            id: '',
            idd: '',
            newReporties:[],
            locationPath:""

        };
    }

    componentDidMount = () => {
        this.autho();
        this.profile();
        this.unassigned();
        console.log(this.state.data)
        console.log(this.props.history.location.pathname)
        this.setState({locationPath:this.props.history.location.pathname})
    }

    handleChangereportie(e) {

        this.setState({ id: e.target.value });
    }

    autho() {

        let id = this.props.match.params.mid

        fetch(apiconstants.URL + `mng_list/${id}`, {
            method: 'GET',

            headers: {
                'content-Type': 'application/json',
                'Accept': 'application/json',
            },
        })
            .then(res => res.json())
            .then(res => {
                console.log(res[0])
                console.log('res', JSON.stringify(res));

                this.setState({ data: res });
            })

    }

    handleAdd = async (e) => {
        e.preventDefault();
        console.log(this.state.id)
        alert('sucessfully added')
        let id = this.props.match.params.mid
        const response = await axios({
            method: 'PATCH',
            url: apiconstants.URL + `addformng/${this.props.match.params.mid}/${this.state.id}/`,

        })
        console.log(response.data)
        this.setState({ data: response.data });
        this.unassigned();
    }

    handledelete = async (id) => {
        console.log(id);
        let mid = this.props.match.params.mid
        const response = await axios({
            method: 'PATCH',
            //    url: 'http://192.168.1.245:8095/hr ',
            url: apiconstants.URL + `dropformng/${mid}/${id}`,

        })
        console.log(response.data)
        this.setState({ data: response.data });   
        this.unassigned();
    }

    unassigned() {
        fetch(apiconstants.URL + `mngdroplist`, {
            method: 'GET',

            headers: {
                'content-Type': 'application/json',
                'Accept': 'application/json',
            },
        })
            .then(res => res.json())

            .then(res => {


                this.setState({ repo: res });
            })
    }

    profile() {
        let id = this.props.match.params.mid

        fetch(apiconstants.URL + `profile/${id}`, {
            method: 'GET',

            headers: {
                'content-Type': 'application/json',
                'Accept': 'application/json',
            },
        })
            .then(res => res.json())
            .then(res => {
                console.log(res[0])
                console.log('res', JSON.stringify(res));

                this.setState({ data1: res });
                console.log(res)
            })
    }
    setRedirect = () => {
        this.setState({
            redirect: true
        })
    };
    render() {
        const { data1 } = this.state;
        const { repo } = this.state;
        const { data } = this.state;
        const pathURL=this.state.locationPath;
        console.log(this.props)
        var mid = this.props.match.params.mid;
        console.log(mid)
        return (
            <div className="manbg">
                <div>
                    <Header mid={mid} pathURL={pathURL}/>

                </div>

                <br />
                <br />
                <div>
                    <br />
                    <br />


                    <div>




    <div className="profiletab" >
        <img style={{width:"50px"}} src={manager}/>
      &nbsp; &nbsp;
        {data1.empname}<br/>
        {data1.email}
    </div>




                    </div>
                </div>
                {/* <div className="adduser">
                 <h1 style={{ color: "black",size:"35px",fontWeight:"bold"}}>Unassigned Employee</h1>
                 <select className="ui selection dropdown"   style={{width:"auto",color:"black" }} 
                 placeholder='Unassigned Employee'
                 value={this.state.id} 
                 onChange={this.handleChangereportie.bind(this)}>
                       <option value=""  disabled selected>EMPLOYEES</option>
                    {
                    repo.map(row => (

                    <option className="text" style={{ color: "black" }} value={row.id}>{row.empname}</option> 
                        ))
                    }
                </select>
                <Button color="green" style={{color:"add",width:"auto",marginBottom:"200px"}} onClick={this.handleAdd}><Icon name="add user"/>Add</Button>
                </div> */}



<h2 className="listofr" style={{  color: "black",  }}>List of Reportees </h2>
<br/><br/><br/>

                <div className="container" style={{ width: "600px" }}>
                    <ul className="responsive-table" style={{ color: "black" }}>
                        <li className="table-header">

                            <div className="col col-1">Employee ID</div>
                            <div style={{paddingRight:"170px"}} >Employee<br/> Name</div>
                        </li>


                        {
                            data.map((row) => (
                                <li className="table-row">
                                    <div className="col col-1">
                                        {row.id}
                                    </div>
                                    <div  className="col col-2"> 
                                        <Link  to={"/month" + "/" + this.props.match.params.mid + "/" + row.id + "/" + row.empname + "/" + data1.empname} >{row.empname}</Link> 
                                        </div>

                                       {/* <div className="col col-3">
                                        <Button circular color='red' icon='remove' style={{width:"auto",float:"right"}} value={row.id} onClick={() => this.handledelete(row.id)} />

                                            </div> */}
                                </li>
                            ))
                        }

                    </ul>
                </div>
                <br />
                <br />
                <br />
                <br />
                <br />
                <br />
                <br />
                <br />
                <br />
                <Footer />
            </div>
        );

    }

}





export default Managermain
