import React, { Component } from 'react';
import { Form, TextArea, Button, Segment,Icon,Label } from 'semantic-ui-react';
import Managerheader1 from "../Managerpg1/Managerheader1";
import ManagerFooter2 from "../Managerpg2/ManagerFooter2";
// import './Managermain4.css';
import "../Managerpg1/Managermain.css"
import axios from 'axios';
import { Redirect } from "react-router-dom";
import apiconstants from '../employee/config';

var bgColors = {
  "Default": "#81b71a",
  "Blue": "#00B1E1",
  "Cyan": "#",
  "Green": "#98FB98",
  "Red": "#E9573F",
  "Yellow": "#d3d3d3",
};


class Managermain4 extends Component {
  constructor() {
    super();

    this.state = {
      // id: 26,
      // month: "aug",
      dynamic: [],

    };
    //console.log("ehehe ",this.state.dynamic)
  }
  //  handleNameChange = evt => {
  //  this.setState({ name: evt.target.value });
  //};
  /* componentDidMount=()=>{
     this.autho();
 }

   autho() {
     //const {navigate} = this.props.navigation;
