     fetch(url, {
         method: 'GET',
         // body: JSON.stringify(this.state),
         headers: {
             'content-Type': 'application/json',
             'Accept': 'application/json',
         },
     }).then(response => response.json())
         .then(res => {
           console.log(res[0])
           console.log('res', JSON.stringify(res));
           this.setState({data:res});

           console.log(row.ques)
         })
 }*/
  componentWillMount() {
    let id=this.props.match.params.id
    let month=this.props.match.params.month
    const response = axios({
      method: 'post',
      // url: 'http://192.168.1.245:8095/updatemonthvalue',
      url: apiconstants.URL + `updatemonthvalue/${id}/${month}`,
      //  data: this.state
    })
      .then(response => {
        console.log(response)

        if (response.data.length != 0) {
          const da = [];
          console.log(JSON.stringify(response.data))
          response.data.map((datas)=>{
            if(datas.type==='d'){
              da.push(datas);
            }
          })
          this.setState({ dynamic: da })


          //  this.setState({a:response.data.ques})
          //  this.setState({remark:response.data.ans})

        }

        else {

          // console.log("second",response)
          this.setState({ dynamic: this.state.dynamic.concat({ id: this.props.match.params.id, ques: "", ans: "", remark: "", month: this.props.match.params.month }) });


        }
      })

  }


  handleQuesChange = i => evt => {
    const newques = this.state.dynamic.map((row, j) => {
      if (i !== j) return row;
      console.log('row', row)
      return { ...row, ques: evt.target.value };
    });

    this.setState({ dynamic: newques });
  };
  handleAnsChange = i => evt => {
    const newans = this.state.dynamic.map((row, j) => {
      if (i !== j) return row;
      return { ...row, ans: evt.target.value };
    });

    this.setState({ dynamic: newans });
  };
  handleRemarkChange = i => evt => {
    const newr = this.state.dynamic.map((row, j) => {
      if (i !== j) return row;
      return { ...row, remark: evt.target.value };
    });

    this.setState({ dynamic: newr });
  };



  handleSubmit = e => {
    e.preventDefault();
    this.setState({
      redirect: true
    })
    console.log(JSON.stringify(this.state.dynamic));

    let id=this.props.match.params.id
    let month=this.props.match.params.month
    console.log(this.state.dynamic, 'state');
    fetch(apiconstants.URL + `qanda_dynamic/${id}/${month}`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(this.state.dynamic)

    })
    console.log(this.state.dynamic)

    // .then(res=>{
    //     console.log(res)
    // }).then(response=>console.log(response))

    // this.setState({
    //   redirect: true
    // })

  };


  handleAdd = () => {
    this.setState({
      dynamic: this.state.dynamic.concat({ id:this.props.match.params.id, ques: "", ans: "", remark: "", month:this.props.match.params.month })
      //dynamic: this.state.dynamic.concat([{ a: "" }]),
      //  dynamic: this.state.dynamic.concat([{remark: "" }])

    });
  };
  //   dynamic: [{id:3, q:'',a:'',remark:'' }],




  setRedirect1 = (event) => {
    event.preventDefault();
   console.log(this.state)
    this.setState({
        redirect1: true
    })
    //window.location.reload();
};


  render() {
    // const {id}=11
    // const {month}="oct"
    {
      if (this.state.redirect) {
        return <Redirect to={"/goals" +"/"+  (this.props.match.params.id)+"/" +(this.props.match.params.month)+"/"+(this.props.match.params.pro)} />;

      }
    }


    {
      if (this.state.redirect1) {
        return <Redirect to={"/static-form" +"/"+  (this.props.match.params.id)+"/" +(this.props.match.params.month)+ "/" +(this.props.match.params.emp)+"/"+ (this.props.match.params.emp)} />;

      }
    }

    return (
      <div className="manbg">
        <Managerheader1 />

        <br />
        <br />
        <br />
        <h1 style={{color:"	#303030"}}>Employee Feedback-2</h1>
        <br />
        <Label size='large' style={{color:"black",width :"auto"}} ><Icon name='user' />

       {this.props.match.params.pro}
    </Label> 
        <br />

        <Form >
          {this.state.dynamic.map((row, i) => (
            <div style={{ paddingLeft: "10%" }} className="form1">

              <TextArea style={{ color: "black",height:"60px" }}
                placeholder="enter your question"
                value={row.ques}

                onChange={this.handleQuesChange(i)}
              >{row.ques}</TextArea>
              <br />

              <TextArea style={{ color: "black" }}
                placeholder='enter answer here'
                value={row.ans}
                style={{ backgroundColor: bgColors.Yellow }}
                onChange={this.handleAnsChange(i)}
              >{row.ans}</TextArea>
              <br />

              <TextArea style={{ color: "black",height:"60px"}}
                placeholder='Remarks. if any?'
                style={{ backgroundColor: bgColors.Green }}
                value={row.remark}
                onChange={this.handleRemarkChange(i)}
              >{row.remark}</TextArea>



            </div>
          ))}
          <br/>
          <br/>
          <br/>

          <button onClick={this.setRedirect1}
                            style={{ width :"auto",}}
                            className="ui blue button"> <Icon name="arrow circle left" />Go Back</button>



          <Button
            type="button"
            onClick={this.handleAdd}
            className="ui blue button"
            style={{width:"auto"}} > Add more questions </Button>

          <Button onClick={this.handleSubmit}  style={{width:"auto"}}   className="ui green button" >   Save <Icon name="arrow circle right" /></Button>



        </Form>
        <br />
        <br />
        <br />
        <br />
        <br />
        <br />
        <br />
        <ManagerFooter2 />
      </div>

    );
  }
}

export default Managermain4;
