import React from 'react';
import './Pop.css'
export default class Pop extends React.Component {
  render() {
    return (
      <div className='popup'>
        <div className='popup_inner'>
          <h1>{this.props.text}</h1>
          <input type="text" value="Reason why are you disagrreing" />
          <button onClick={this.props.closePopup}>Disagree</button>
        </div>
      </div>
    );
  }
}
