import axios from "axios";
import React from 'react';
import { Redirect } from "react-router-dom";
import { Button, Form, Icon, Label, TextArea } from 'semantic-ui-react';
import Footer from "../Components/Footer";
import Header from "../Components/Header";
import apiconstants from '../employee/config';
import "../Managerpg1/Managermain.css";


var bgColors = {
    "Green": "#8CC152",
    "Yellow": "#d3d3d3",
};

export default class Test extends React.Component {
    constructor(props) {
        super(props);
        this.state = {

            question: ' How is everything going today? Tell me about this last week?',
            answer: '',
            question: ' What has gone well/not so well for you in this month?',
            answer: '',
            question: ' What challenges have you faced?',
            answer: '',
            question: ' How are you/your team progressing towards established goals?',
            answer: '',
            question: ' What are you committing to between now and the next time we meet?',
            answer: '',
            disabled: false,
            dynamic: [],
            disabled: false,
            nullValue: false

        }
    }


    componentWillMount() {
        var id = this.props.match.params.id
        var month = this.props.match.params.month
        var year = "2019"
        console.log(id)
        const response = axios({
            method: 'post',
            // url: 'http://192.168.1.245:8095/updatemonthvalue',
            url: apiconstants.URL + `updatemonthvalue/${id}/${month}/${year}`,
            data: this.state
        })
            .then(response => {
                console.log(JSON.stringify(response.data + "  first"))
                if (response.data.length != 0) {
                    console.log(JSON.stringify(response.data.answer))
                    this.setState({ a1: response.data[0].answer });
                    this.setState({ a2: response.data[1].answer });
                    this.setState({ a3: response.data[2].answer });
                    this.setState({ a4: response.data[3].answer });
                    this.setState({ a5: response.data[4].answer });
                }
                else {
                    console.log("second", response)
                    this.setState({ a1: '' });
                    this.setState({ a2: '' });
                    this.setState({ a3: '' });
                    this.setState({ a4: '' });
                    this.setState({ a5: '' });
                }


            if (response.data.length != 0) {
                const da = [];
                console.log(JSON.stringify(response.data))
                response.data.map((datas) => {
                  if (datas.type === 'd') {
                    da.push(datas);
                  }
                })
                this.setState({ dynamic: da })
              }

              else {
                this.setState({ dynamic: this.state.dynamic.concat({ id: this.props.match.params.id, question: "", answer: "", remark: "", month: this.props.match.params.month }) });

              }
            })

    }


    // handelAnswerchange1 = (event) => {

    //     this.setState({
    //         a1: event.target.value

