          const newGoals= this.state.dynamic.map((row, j) => {
            if (i !== j) return row;
            return { ...row, goal: evt.target.value};
          });

          this.setState({ dynamic: newGoals });
        };
        handleDeadlineChange = i => evt => {
            const newDeadline= this.state.dynamic.map((row, j) => {
