import React from 'react'
import Managerheader1 from './Managerheader1'
//import NINE from "../nl_logo.png";
import 'semantic-ui/dist/semantic.min.css';
//import {Button} from 'semantic-ui-react';
import './Managermain.css'
import remov from './red_remov.png'
import ManagerNav from './ManagerNav'
//import login from './login.png';
import ManagerFooter from "./ManagerFooter";
import { Link } from "react-router-dom";
//import {Redirect} from "react-router-dom";
//import axios from 'axios';



class Managermain extends React.Component {

    constructor(props) {
        super(props);

        //  this.state.products = [];
        this.state = {
            data: [],

            empname: '',
            email: '',
            phone: '',
            designation: '',

        };


    }




    componentDidMount = () => {
        this.autho();
    }

    autho() {
        //const {navigate} = this.props.navigation;
        var url =  apiconstants.URL +'mng_list';
        const id = localStorage.getItem("id")

        fetch(url, {
            method: 'POST',
            body: JSON.stringify({
                mid: id
            }),
            headers: {
                'content-Type': 'application/json',
                'Accept': 'application/json',
            },
        })
        .then(res => res.json())

            .then(res => {
                console.log(res[0])
                console.log('res', JSON.stringify(res));
                //console.log(JSON.stringify(res.data)),
                this.setState({ data: res });
                //localStorage.setItem("id", res.id)
                // localStorage.setItem("id", res.id),
                // localStorage.setItem("empname", res.empname)

            })

            }


    setRedirect = () => {
        this.setState({
            redirect: true
        })
        //window.location.reload();
    };


    render() {

        const { data } = this.state;
        const id = localStorage.getItem("id");
        console.log(data.length);
        console.log(data);
        console.log(localStorage.getItem("empname"));

        return (



            <div className="manbg">
                <div>
                    <Managerheader1 />
                    <ManagerNav />
                </div>

                <br />
                <br />



                <div>
                    <br />
                    <br />


                    <div>
                        <div className="jumbotron" className="profile" style={{ paddingLeft: "5%", width: "100%", height: "200px" }} >

                            <p>Name: {localStorage.getItem("empname")}</p>
                            <p>Email:{localStorage.getItem("email")}</p>
                            <p>Contact:{localStorage.getItem("phone")}</p>


                        </div>
                        <br />
                    </div>

                    <br />
                </div>





                <br />
                <br />


                <h2 className="ui" style={{ width: "50%", color: "black" }}>List of Reportees</h2>
                <table style={{ width: "50%", color: "black" }} className="ui">
                    <thead>

                        <tr>
                            <th>EMPLOYEE ID</th>
                            <th>EMPLOYEE NAME</th>

                        </tr>
                    </thead>
                    <tbody style={{ color: "black" }}>
                        {
                            data.map(row => (
                                <tr style={{ color: "black" }}>
                                    <td>{row.id}</td>
                                    <td>  <Link to="/month">{row.empname}</Link></td>


                                </tr>
                            ))
                        }
                    </tbody>
                </table>
                <br />
                <br />
                <br />
                <br />
                <br />
                <br /> <br />
                <br />
                <br />


                <ManagerFooter />
            </div>
        );

    }

}

class ProductTable extends React.Component {

    render() {
        var onProductTableUpdate = this.props.onProductTableUpdate;
        var rowDel = this.props.onRowDel;
        var filterText = this.props.filterText;
        var product = this.props.products.map(function (product) {
            if (product.name.indexOf(filterText) === -1) {
                return;
            }
            return (<ProductRow onProductTableUpdate={onProductTableUpdate}
                product={product}
                onDelEvent={rowDel.bind(this)}
                key={product.id} />)
        });
        return (
            <div>
                <br />
                <br />
                <br />
                <br />
                <br />
                <br />
                <div className={'ui'} >
                    <div className={' ui blue inverted table '}
                        style={{ color: "black" }} >



                        <tbody>
                            <th>Emp ID</th>
                            <th>Name</th>

                            {product}

                        </tbody>

                    </div>


                    <button type="button"
                        onClick={this.props.onRowAdd}
                        className="btn btn-success pull-right">Add</button>


                    <button type="button"
                        onClick={this.setRedirect}
                        className="btn btn-success pull-right">Next</button>


                    <br />
                    <br />
                    <br />

                </div>
            </div>


        );

    }

}

class ProductRow extends React.Component {
    onDelEvent() {
        this.props.onDelEvent(this.props.product);

    }
    render() {

        return (
            <tr className="eachRow" style={{ color: "black" }}>
                <EditableCell onProductTableUpdate={this.props.onProductTableUpdate} cellData={{
                    type: "name",
                    value: this.props.product.name,
                    id: this.props.product.id

                }} />
                <EditableCell onProductTableUpdate={this.props.onProductTableUpdate} cellData={{
                    type: "price",
                    value: this.props.product.price,
                    id: this.props.product.id
                }} />

                <td className="d">
                    <img src={remov}
                        onClick={this.onDelEvent.bind(this)}

                        className="del-btn" />
                </td>
            </tr>
        );

    }

}
class EditableCell extends React.Component {

    render() {
        return (
            <td>
                <input name={this.props.cellData.type}
                    id={this.props.cellData.id}
                    value={this.props.cellData.value}
                    onChange={this.props.onProductTableUpdate} />
            </td>

        );

    }

}



export default Managermain
