import React from 'react'
import Managerheader1 from './Managerheader1'
//import NINE from "../nl_logo.png";
import 'semantic-ui/dist/semantic.min.css';
//import {Button} from 'semantic-ui-react';
import './Managermain.css'
import remov from './red_remov.png'
import ManagerNav from './ManagerNav'
//import login from './login.png';
import ManagerFooter from "./ManagerFooter";
import { Link } from "react-router-dom";
//import {Redirect} from "react-router-dom";
//import axios from 'axios';
import apiconstants from '../employee/config';
import { Form, TextArea, Button, Segment, Icon, Label, Table, Header,Dropdown } from 'semantic-ui-react';
import axios from 'axios'

class Managermain extends React.Component {

    constructor(props) {
        super(props);

        //  this.state.products = [];
        this.state = {
            data: [],
            data1: '',
            repo: [],
            empname: '',
            email: '',
            phone: '',
            designation: '',
            id: 0,
            idd: '',
            newReporties:[],
            locationPath:""

        };
    }

    componentDidMount = () => {
        this.autho();
        this.profile();
        this.unassigned();
        console.log(this.state.data)
        console.log(this.props.history.location.pathname)
        this.setState({locationPath:this.props.history.location.pathname})
    }

    handleChangereportie(e) {

        this.setState({ id: e.target.value });
    }

    autho() {
        //const {navigate} = this.props.navigation;
        // var url =  apiconstants.URL +'mng_list/' + id;
        let id = this.props.match.params.mid

        fetch(apiconstants.URL + `mng_list/${id}`, {
            method: 'GET',
            // body: JSON.stringify({
            //     // mid: id
            // }),
            headers: {
                'content-Type': 'application/json',
                'Accept': 'application/json',
            },
        })
            .then(res => res.json())

            .then(res => {
                console.log(res[0])
                console.log('res', JSON.stringify(res));
                //console.log(JSON.stringify(res.data)),
                this.setState({ data: res });
            })

    }

    handleAdd = async (e) => {
        e.preventDefault();
        console.log(this.state.id)
        let id = this.props.match.params.mid
        const response = await axios({
            method: 'PATCH',
            //    url: 'http://192.168.1.245:8095/hr ',
            url: apiconstants.URL + `addformng/${id}/${this.state.id}`,
            //   data:36
        })
        console.log(response.data)
        this.setState({ data: response.data });
    }

    handledelete = async (id) => {
        console.log(id);
        let mid = this.props.match.params.mid
        const response = await axios({
            method: 'PATCH',
            //    url: 'http://192.168.1.245:8095/hr ',
            url: apiconstants.URL + `dropformng/${mid}/${id}`,
            //   data:36
        })
        console.log(response.data)
        this.setState({ data: response.data });   
    }

    unassigned() {
        fetch(apiconstants.URL + `mngdroplist`, {
            method: 'GET',
            // body: JSON.stringify({
            //     // mid: id
            // }),
            headers: {
                'content-Type': 'application/json',
                'Accept': 'application/json',
            },
        })
            .then(res => res.json())

            .then(res => {

                //console.log('res', JSON.stringify(res));
                //console.log(JSON.stringify(res.data)),
                this.setState({ repo: res });
            })
    }

    profile() {
        let id = this.props.match.params.mid

        fetch(apiconstants.URL + `profile/${id}`, {
            method: 'GET',
            // body: JSON.stringify({
            // // mid: id
            // }),
            headers: {
                'content-Type': 'application/json',
                'Accept': 'application/json',
            },
        })

            .then(res => res.json())
            .then(res => {
                console.log(res[0])
                console.log('res', JSON.stringify(res));
                //console.log(JSON.stringify(res.data)),
                this.setState({ data1: res });
                console.log(res)
            })
    }


    setRedirect = () => {
        this.setState({
            redirect: true
        })
    };



    render() {

        const { data1 } = this.state;
        const { repo } = this.state;
        const { data } = this.state;
        const pathURL=this.state.locationPath;
        console.log(this.props)
        var mid = this.props.match.params.mid;
        console.log(mid)
        return (



            <div className="manbg">
                <div>
                    <Managerheader1 mid={mid} pathURL={pathURL}
                    />

                </div>

                <br />
                <br />
                <div>
                    <br />
                    <br />


                    <div>


                        <Label style={{ color: "black", width: "100%", fontSize: "25px" }} >
                            <Icon name='big user' /><br /><br />


                            Name: {data1.empname}
                            <br />
                            Email: {data1.email}


                        </Label>


                        {/* </div> */}
                        <br />
                    </div>
                    <br />
                </div>
                <br />
                <br />
                <h2 className="ui" style={{ width: "50%", color: "black", textAlign: "center" }}>List of Reportees</h2>
                {/* <table style={{ width: "50%", color: "black" }} className="ui">
                    <thead>
                        <tr>
                            <th>EMPLOYEE ID</th>
                            <th>EMPLOYEE NAME</th>
                        </tr>
                    </thead> */}
                    <div className="ocenter">
                <h1 style={{ color: "black" }}>unassigned employee</h1>
                <select style={{ color: "black" }} name="reportie" value={this.state.id} onChange={this.handleChangereportie.bind(this)}>
                    {
                        repo.map(row => (
                            <option style={{ color: "black" }} value={row.id}>{row.empname}</option>
                        ))
                    }
                </select>
                <button onClick={this.handleAdd}>add</button>
                </div>



                {/* <Dropdown
    text='Add user'
    icon='add user'
    floating
    labeled
    button
    className='icon'
    style={{color:"black"}}
  >
    <Dropdown.Menu>
      <Dropdown.Header style={{color:"black"}} />
      {repo.map((row) => (
        <Dropdown.Item  key={row.empname} {...row} />
      ))}
    </Dropdown.Menu>
  </Dropdown> */}


                <div style={{ width: "600px" }}>
                    <table class="center" style={{ paddingLeft: "300px", color: "black" }}>
                        <tr>

                            <th>Employee ID</th>
                            <th>Employee Name</th>
                        </tr>


                        {/* <tbody style={{ color: "black" }}>
                        {
                            data.map(row => (
                                 <tr style={{ color: "black" }}>
                                    <td>{row.id}</td>

                                    <td>  <Link to={"/month" +"/" + row.id +"/"+ row.empname } >{row.empname}</Link></td>
                                </tr>
                            ))
                        }
                    </tbody> */}

                        {
                            data.map((row) => (
                                <tr>
                                    <td>

                                        {row.id}
                                    </td>
                                    <td>
                                        <Link to={"/month" + "/" + this.props.match.params.mid + "/" + row.id + "/" + row.empname + "/" + data1.empname} >{row.empname}</Link> <button value={row.id} onClick={() => this.handledelete(row.id)}><Icon name="user delete"/></button></td>
                                </tr>
                            ))
                        }

                    </table>
                </div>



                <br />
                <br />
                <br />
                <br />
                <br />
                <br />
                <br />
                <br />
                <br />


                <ManagerFooter />
            </div>
        );

    }

}





