import React from 'react'
import { Form, TextArea,Icon ,Label,Button} from 'semantic-ui-react'
import Header from "../Components/Header";
import Footer from"../Components/Footer";
import ManagerNav from "../Managerpg1/ManagerNav"
import "../Managerpg1/Managermain.css"
import { Redirect } from "react-router-dom";
import axios from "axios"
import apiconstants from '../employee/config';

import ManagerFooter from '../Managerpg1/ManagerFooter'

var bgColors = {
    "Green": "#8CC152",
    "Yellow": "#d3d3d3",
};

export default class Test extends React.Component {
    constructor(props) {
        super(props);
        this.state = {

            q1: ' How is everything going today? Tell me about this last week?',
            a1: '',
            q2: ' What has gone well/not so well for you in this month?',
            a2: '',
            q3: ' What challenges have you faced?',
            a3: '',
            q4: ' How are you/your team progressing towards established goals?',
            a4: '',
            q5: ' What are you committing to between now and the next time we meet?',
            a5: '',
            disabled: false,
            dynamic: [],
            disabled: false,
            nullValue: false

        }
    }


    componentWillMount() {
        var id = this.props.match.params.id
        var month = this.props.match.params.month

        console.log(id)
        const response = axios({
            method: 'post',
            // url: 'http://192.168.1.245:8095/updatemonthvalue',
            url: apiconstants.URL + `updatemonthvalue/${id}/${month}`,
            data: this.state
        })
            .then(response => {
                console.log(JSON.stringify(response.data + "  first"))
                if (response.data.length != 0) {
                    console.log(JSON.stringify(response.data.answer))
                    this.setState({ a1: response.data[0].answer });
                    this.setState({ a2: response.data[1].answer });
                    this.setState({ a3: response.data[2].answer });
                    this.setState({ a4: response.data[3].answer });
                    this.setState({ a5: response.data[4].answer });
                }
                else {
                    console.log("second", response)
                    this.setState({ a1: '' });
                    this.setState({ a2: '' });
                    this.setState({ a3: '' });
                    this.setState({ a4: '' });
                    this.setState({ a5: '' });
                }
            })

            if (response.data.length != 0) {
                const da = [];
                console.log(JSON.stringify(response.data))
                response.data.map((datas) => {
                  if (datas.type === 'd') {
                    da.push(datas);
                  }
                })
                this.setState({ dynamic: da })
              }

              else {
                this.setState({ dynamic: this.state.dynamic.concat({ id: this.props.match.params.id, question: "", answer: "", remark: "", month: this.props.match.params.month }) });

              }

    }


    handelAnswerchange1 = (event) => {

        this.setState({
            a1: event.target.value

        })
    };


    handelAnswerchange2 = (event) => {
        this.setState({
            a2: event.target.value
        })
    };

    handelAnswerchange3 = (event) => {
        this.setState({
            a3: event.target.value
        })
    };

    handelAnswerchange4 = (event) => {
        this.setState({
            a4: event.target.value
        })
    };

    handelAnswerchange5 = (event) => {
        this.setState({
            a5: event.target.value
        })
    };


    handleQuesChange = i => evt => {
        const newques = this.state.dynamic.map((row, j) => {
          if (i !== j) return row;
          console.log('row', row)
          return { ...row, question: evt.target.value };
        });

        this.setState({ dynamic: newques });
      };
      handleAnsChange = i => evt => {
        const newanswer = this.state.dynamic.map((row, j) => {
          if (i !== j) return row;
          return { ...row, answer: evt.target.value };
        });

        this.setState({ dynamic: newanswer });
      };
      handleRemarkChange = i => evt => {
        const newr = this.state.dynamic.map((row, j) => {
          if (i !== j) return row;
          return { ...row, remark: evt.target.value };
        });

        this.setState({ dynamic: newr });
      };





    setRedirect = (event) => {
        event.preventDefault();
        console.log(this.state)
        let id = this.props.match.params.id
        let month = this.props.match.params.month

        if (this.validate() == true) {
            const response = axios({
                method: 'PATCH',
                // url:'http://192.168.1.245:8095/qanda_static',
                url: apiconstants.URL + `qanda_static/${id}/${month}`,
                data: this.state


            });
            // this.setState({
            //     redirect: true
            // })
        }

        for(let i=0;i<this.state.dynamic.length;i++){
            if((this.state.dynamic[i].question=='' && this.state.dynamic[i].answer=='' && this.state.dynamic[i].remark==''))
            {
               this.setState({nullValue:false})
              this.setState({
                  redirect: true
                })

            }
            else if((this.state.dynamic[i].question==''|| this.state.dynamic[i].answer==''  ))
            {
             this.setState({nullValue:true})
              break;
            }


          if(this.state.nullValue==true){
            alert("Fields can't be empty")

          }
          else{
            let id = this.props.match.params.id
          let month = this.props.match.params.month
          console.log(this.state.dynamic, 'state');
          fetch(apiconstants.URL + `qanda_dynamic/${id}/${month}`, {
            method: 'PATCH',
            headers: {
              'Content-Type': 'application/json'
            },
            body: JSON.stringify(this.state.dynamic)

          }).then(res=> res)
          .then(res => {
            console.log(res.status)

            })
            // this.setState({
            //   redirect: true
            // })

            //    if (this.state.redirect) {
            //     return <Redirect to={"/goals" + "/" + (this.props.match.params.mid) + "/" + (this.props.match.params.id) + "/" + (this.props.match.params.month) + "/" + (this.props.match.params.emp) + "/" + (this.props.match.params.pro)} />;

            //   }

          }

        };





    };

    async componentDidMount() {
        console.log("Component Did mount started")
        let val=await this.time();
        console.log("value is "+val)
        if(val===1){
            console.log("it entered the if "+val)
            await setTimeout(()=>{this.setState({disabled:true})},);
        }

    }

    time = async () => {
        console.log("state is:"+JSON.stringify(this.state));

        var id = this.props.match.params.id
        var month = this.props.match.params.month

        const response =await axios({

            method: 'get',
            url: apiconstants.URL + `/employeetimestamp/${id}/${month}`,
            // data: this.state
        })
            console.log(month)
            return await response.data.time;
    }


    setRedirect1 = (event) => {
        event.preventDefault();
       console.log(this.state)
        this.setState({
            redirect1: true
        })

    };





    validate() {
        const { q1 } = this.state;
        const { a1 } = this.state;
        const { q2 } = this.state;
        const { a2 } = this.state;
        const { q3 } = this.state;
        const { a3 } = this.state;
        const { q4 } = this.state;
        const { a4 } = this.state;
        const { q5 } = this.state;
        const { a5 } = this.state;

        let formIsValid = true;
        if (q1 == '' || a1 == '' || q2 == '' || a2 == '' || q3 == '' || a3 == '' || q4 == '' || a4 == '' || q5 == '' || a5 == '') {
            alert('Please fill the field');
            formIsValid = false;
        }
        else
            return formIsValid;
    }

    render() {
        {
            var id = this.props.match.params.id
            var month = this.props.match.params.month
            if (this.state.redirect) {
                return <Redirect to={"/dynamic-form"+"/"+  (this.props.match.params.mid) + "/" + (this.props.match.params.id) + "/" + (this.props.match.params.month)+"/"+(this.props.match.params.emp) +"/"+(this.props.match.params.pro)} />;

            }
        }

        {
            var id = this.props.match.params.id
            var month = this.props.match.params.month
            if (this.state.redirect1) {
                return <Redirect to={"/month" + "/" + (this.props.match.params.mid) + "/" + (this.props.match.params.id) +
                 "/" + (this.props.match.params.emp)+"/"+ (this.props.match.params.pro)} />;

            }
        }
        var mid = this.props.match.params.mid;
        return (
            <div className="manbg">
                <Header mid={mid} />
                <Label size='large' style={{color:"black",width :"auto"}} ><Icon name='user' />

       {this.props.match.params.pro}
       </Label> 


               <h1 style={{color:"#303030",paddingLeft:"40px"}}>Employee Feedback-1 : <span style={{color:"darkblue",fontWeight:"bolder"}}>{this.props.match.params.emp}</span> </h1>
                <br />
                <br />
<div className="formalign">
                <Form >
                    <input value={this.state.q1} />
                    <TextArea style={{ backgroundColor: bgColors.Yellow }}
                        placeholder='Answers'
                        value={this.state.a1}
                        disabled={this.state.disabled}
                        onChange={this.handelAnswerchange1}

                    />
                </Form>


                <Form>
                    <input value={this.state.q2} />
                    <TextArea style={{ backgroundColor: bgColors.Yellow }}
                        placeholder='Answers'
                        value={this.state.a2}
                        disabled={this.state.disabled}
                        onChange={this.handelAnswerchange2}
                    />
                </Form>


                <Form>
                    <input value={this.state.q3} />
                    <TextArea style={{ backgroundColor: bgColors.Yellow }}
                        placeholder='Answers'
                        value={this.state.a3}
                        disabled={this.state.disabled}
                        onChange={this.handelAnswerchange3} />
                </Form>


                <Form>
                    <input value={this.state.q4} />
                    <TextArea style={{ backgroundColor: bgColors.Yellow }}
                        placeholder='Answers'
                        disabled={this.state.disabled}
                        value={this.state.a4}
                        onChange={this.handelAnswerchange4} />
                </Form>



                <Form>
                    <input value={this.state.q5} />
                    <TextArea style={{ backgroundColor: bgColors.Yellow }}
                        placeholder='Answers'
                        value={this.state.a5}
                        disabled={this.state.disabled}
                        onChange={this.handelAnswerchange5} />
                </Form>
                </div>
                <br />
                <br />
                <br />
                <br />

                <button onClick={this.setRedirect1}
                            style={{ width :"auto",}}
                            className="ui blue button"> <Icon name="arrow circle left" />Go Back</button>
                <button onClick={this.setRedirect}
                    style={{ "width": "200px",float:"right" }}
                    className="ui positive button">Save & Next <Icon name="arrow circle right" /> </button>
                <br />
                <br />
                <br />
                <br />





<h1 style={{ color: " #303030" }}>Employee Feedback-2 :<span style={{color:"darkblue",fontWeight:"bolder"}}>{this.props.match.params.emp}</span> </h1>
          <br />
               <Form >
            {this.state.dynamic.map((row, i) => (
              <div style={{ paddingLeft: "10%" }} className="form1">

                <TextArea style={{ color: "black", height: "60px" }}
                  placeholder="Enter your question"
                  value={row.question}
                  disabled={this.state.disabled}
                  onChange={this.handleQuesChange(i)}
               required >{row.question}</TextArea>
                <br />

                <TextArea style={{ color: "black" }}
                  placeholder='Enter answer here'
                  value={row.answer}
                  disabled={this.state.disabled}
                  style={{ backgroundColor: bgColors.Yellow }}
                  onChange={this.handleAnsChange(i)}
                  required >{row.answer}</TextArea>
                <br />

                <TextArea style={{ color: "black", height: "60px" }}
                  placeholder='Remarks. If any?'
                  style={{ backgroundColor: bgColors.Green }}
                  value={row.remark}
                  disabled={this.state.disabled}
                  onChange={this.handleRemarkChange(i)}
                  required  >{row.remark}</TextArea>
              </div>
            ))}

            <button onClick={this.setRedirect1}
              style={{ width: "auto", }}
              className="ui blue button"> <Icon name="arrow circle left" />Go Back</button>

            <Button
              type="button"
              onClick={this.handleAdd}
              className="ui blue button"
              style={{ width: "auto" }} > Add more questions </Button>

            <Button onClick={this.handleSubmit} style={{ width: "auto" }} className="ui green button" >   Save <Icon name="arrow circle right" /></Button>
          </Form>

          <Footer/>
        </div>

        )
    }

}


