import React,{Component} from 'react';
import './DropDown.css';
import './hr.css';
import axios from 'axios';

class DropDown extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            id:null,
            month:null,
            progress:null,
           data:[],
           data1:[]
        };
    }
   handleChangeid(e) {

       this.setState({id: e.target.value});
     }
     handleChangemonth(e) {

        this.setState({
            month: e.target.value
        })


      }
      handleChangeprogress(e) {

        this.setState({progress: e.target.value});

      }

    componentDidMount = () => {
        const {data}=this.state
       this.autho();
       this.autho2();

    }

    autho2() {
        //const {navigate} = this.props.navigation;
        var url = `http://192.168.1.245:8095/hrviewall`;

        fetch(url, {
            method: 'GET',
            // body: JSON.stringify(this.state),
            headers: {
                'content-Type': 'application/json',
                'Accept': 'application/json',
            },
        }).then(response => response.json())
            .then(res => {
                console.log(res[0])
                console.log('res', JSON.stringify(res));
                this.setState({data:res});

            })

}
      autho() {
        //const {navigate} = this.props.navigation;
      var url = `http://192.168.1.245:8095/hremployeeidview`;

        fetch(url, {
            method: 'GET',
            // body: JSON.stringify(this.state),
            headers: {
                'content-Type': 'application/json',
                'Accept': 'application/json',
            },
        }).then(response => response.json())
        .then(res => {
            console.log(res[0])
            console.log('res', JSON.stringify(res));
            this.setState({data1:res});

        })
}

handleSubmit =async( e) => {
            e.preventDefault();
            console.log(this.state)
            const {id,month,progress} =  this.state;
           const response = await axios({
                method: 'post',
               url: 'http://192.168.1.245:8095/hr ',

                data: this.state
            })
            this.setState({data:response.data})

            console.log(response.data) 


       /*   const resposne = fetch('http://192.168.1.245:8095/hr',{
                   method:'POST',
                    headers:{
                        'Content-Type':'application/json'
                    },

                    body:JSON.stringify(this.state)

                }).then(response=>{
                   response.json()

                    console.log(response)
                }) .then(response => {

                    console.log('response', JSON.stringify(response));
                    this.setState({response:response});
                    console.log(response)

                }).catch(function(error) 
                {console.log(error)});
        };*/



    };
    render() {
        const {data1}=this.state;
        const {data}=this.state;
        console.log(data);
        return (
            <div>
                <span  className="emp_id">
               EMP_ID


             <select name="id" value={this.state.id} onChange={this.handleChangeid.bind(this)}>
             <option value="null" selected>none</option>
             {
                data1.map(row => (
                  <option value={row.id}>{row.id}</option>
                  ))

                }
              </select>


                </span>
                <span className="month">
           MONTH
            <select style={{color:"black"}} name="month" value={this.state.month}  onChange={this.handleChangemonth.bind(this)}>
            <option value="null" selected>none</option>    
            <option value="jan">jan </option>
            <option value="feb">feb</option>
            <option value="mar" >mar </option>
            <option value="apr">apr</option>
            <option value="may">may</option>
            <option value="jun">jun</option>
            <option value="jul">jul</option>
            <option value="aug">aug</option>
            <option value="sept">sep</option>
            <option value="oct">oct</option>
            <option value="nov">nov</option>
            <option value="dec">dec</option>
        </select>
        </span>
                <span style={{paddingLeft:"20%"}}>
            <input type="checkbox" style={{ paddingLeft:"30%"}} /><span style={{fontSize:"20px"}}>PROGRESS</span>
            <select style={{fontSize:"20px"}} name="progress" value={this.state.progress} onChange={this.handleChangeprogress.bind(this)}> 
              <option value="null" selected>none</option>
                <option value="1">yes</option>
                <option value="0">no</option>
            </select>
</span>
<span  style={{paddingLeft:"8%"}}>
<button class="btn btn-search" type="button" className="search" onClick={this.handleSubmit} ><i class="fa fa-search fa-fw"></i> Search</button>
</span>
<br />
<br />
<div className="table1">
            <table style={{width:"80%"}}>
            <thead>
                <tr>
                    <th>EMPLOYEE ID</th>
                    <th>EMPLOYYEE NAME</th>
                    <th>PROGRESS</th>
                </tr>
            </thead>
            <tbody>
            {
                data.map(row => (
                    <tr>
                        <td >{row.id}</td>
                        <td>{row.empname}</td>
                        <span ><td bgcolor={row.progress==1?"green":"red"} >{row.mon}</td></span>
                    </tr>
                ))

            }

            </tbody>

            </table>
            </div>


            </div>
        );
    }
}
export default DropDown;
