import React,{Component} from 'react';
import { Form, TextArea } from 'semantic-ui-react';
import Managerheader1 from "../Managerpg1/Managerheader1";
import ManagerFooter2 from "../Managerpg2/ManagerFooter2";
import axios from 'axios';

class Managermain4 extends Component {
    constructor() {
      super();

      this.state = {
        dynamic: [],
      };
    //console.log("ehehe ",this.state.dynamic)
        }
      //  handleNameChange = evt => {
      //  this.setState({ name: evt.target.value });
      //};


    handleQuesChange = i => evt => {
      const newques= this.state.dynamic.map((row, j) => {
        if (i !== j) return row;
        return { ...row, q: evt.target.value};
      });

      this.setState({ dynamic: newques });
    };
    handleAnsChange = i => evt => {
        const newans= this.state.dynamic.map((row, j) => {
          if (i !== j) return row;
          return { ...row, a: evt.target.value};
        });

        this.setState({ dynamic: newans});
      };
      handleRemarkChange = i => evt => {
        const newr= this.state.dynamic.map((row, j) => {
          if (i !== j) return row;
          return { ...row, remark: evt.target.value};
        });

        this.setState({ dynamic: newr});
      };

  /*  handleSubmit = evt => {
        evt.preventDefault();
     const {dynamic} = this.state;
     console.log(this.state)
    const response = axios({
        method: 'post',
        url:'http://192.168.1.245:8095/qanda_dynamic',
        data: this.state
    });
console.log(response)
    };*/

    handleSubmit = e => {
        e.preventDefault();
        console.log(JSON.stringify(this.state.dynamic));
        const {id,q,a,remark} = this.state;
        // axios

        //     .post('http://192.168.1.245:8095/qanda_dynamic', JSON.stringify(this.state.dynamic))

        //     .then(response => {
        //         console.log(response)
        //         // return response;

        //     })
        //     .then(response => {
        //         console.log(JSON.stringify(response.data))
        //                   })
        //     .catch(error => {
        //         console.log(error);

        //     })
            fetch('http://192.168.1.245:8095/qanda_dynamic',{
                method:'POST',
                headers:{
                    'Content-Type':'application/json'
                },
                body:JSON.stringify(this.state.dynamic)
            }).then(res=>{
                console.log(res)
            }).then(response=>console.log(response))
    };


    handleAddShareholder = () => {
      this.setState({
        dynamic: this.state.dynamic.concat({ id:100, q:"" ,a:"" , remark:"", month:"jan" })
        //dynamic: this.state.dynamic.concat([{ a: "" }]),
      //  dynamic: this.state.dynamic.concat([{remark: "" }])

      });
    };


    render() {
        const {id}=3
      return (
          <div>
               <Managerheader1/>
               <br />
               <br />
               <br />
               <br />
               <br />

        <form >
          {this.state.dynamic.map((dynamic, i) => (
              <div>

         <TextArea  style={{color:"black"}}
                    placeholder="question" 
                    value={this.state.q}
                    onChange={this. handleQuesChange(i) }
         />
         <TextArea style={{color:"black"}}
                   placeholder='Answers'
                   value={this.state.a}
                   onChange={this. handleAnsChange(i) }
                   />
         <TextArea style={{color:"black"}}
                   placeholder='Remark'
                   value={this.state.remark}
                   onChange={this. handleRemarkChange(i) }

         />

    </div>
          ))}
          <button
            type="button"
            style={{color:"black"}}
            onClick={this.handleAddShareholder}
            className="small"
          >
            Add more questions
          </button>
          <button style={{color:"black"}} onClick={this.handleSubmit} >save</button>

        </form>
        <ManagerFooter2/>
        </div>

      );
    }
  }

  export default Managermain4;
