import React, { Component } from 'react';
import { Form, TextArea, Button, Icon, Label } from 'semantic-ui-react';
import Managerheader1 from "../Managerpg1/Managerheader1";
import ManagerFooter2 from "../Managerpg2/ManagerFooter2";
import "../Managerpg1/Managermain.css"
import axios from 'axios';
import { Redirect } from "react-router-dom";
import apiconstants from '../employee/config';

var bgColors = {
  "Default": "#81b71a",
  "Blue": "#00B1E1",
  "Cyan": "#",
  "Green": "#98FB98",
  "Red": "#E9573F",
  "Yellow": "#d3d3d3",
};


class Managermain4 extends Component {
  constructor() {
    super();

    this.state = {
      dynamic: [],
      disabled: false,
      nullValue: false
    };
  }

  componentWillMount() {
    let id = this.props.match.params.id
    let month = this.props.match.params.month
    let year= '2019' 
    const response = axios({
      method: 'post',
      url: apiconstants.URL + `updatemonthvalue/${id}/${month}/${2019}`,
    })
      .then(response => {
        console.log(response)

        if (response.data.length != 0) {
          const da = [];
          console.log(JSON.stringify(response.data))
          response.data.map((datas) => {
            if (datas.type === 'd') {
              da.push(datas);
            }
          })
          this.setState({ dynamic: da })
        }

        else {
          this.setState({ dynamic: this.state.dynamic.concat({ id: this.props.match.params.id, question: "", answer: "", remark: "", month: this.props.match.params.month }) });

        }
      })

  }
  async componentDidMount() {
    console.log("Component Did mount started")
    let val = await this.time();
    console.log("value is " + val)
    if (val === 1) {
      console.log("it entered the if " + val)
      await setTimeout(() => { this.setState({ disabled: true }) });
    }

  }
  time = async () => {
    console.log("state is:" + JSON.stringify(this.state));

    var id = this.props.match.params.id
    var month = this.props.match.params.month

    const response = await axios({

      method: 'get',
      url: apiconstants.URL + `/employeetimestamp/${id}/${month}`,
    })
    console.log(response.data.time)
    return await response.data.time;
  }

  handleQuesChange = i => evt => {
    const newques = this.state.dynamic.map((row, j) => {
      if (i !== j) return row;
      console.log('row', row)
      return { ...row, question: evt.target.value };
    });

    this.setState({ dynamic: newques });
  };
  handleAnsChange = i => evt => {
    const newanswer = this.state.dynamic.map((row, j) => {
      if (i !== j) return row;
      return { ...row, answer: evt.target.value };
    });

    this.setState({ dynamic: newanswer });
  };
  handleRemarkChange = i => evt => {
    const newr = this.state.dynamic.map((row, j) => {
      if (i !== j) return row;
      return { ...row, remark: evt.target.value };
    });

    this.setState({ dynamic: newr });
  };

  handleSubmit = async (e) => {
    e.preventDefault();
    // this.setState({
    //   redirect: true
    // })
    console.log(JSON.stringify(this.state.dynamic));
    for(let i=0;i<this.state.dynamic.length;i++){
      if((this.state.dynamic[i].question=='' && this.state.dynamic[i].answer=='' && this.state.dynamic[i].remark==''))
      {
        await this.setState({nullValue:false})
        this.setState({
            redirect: true
          })

      }
      else if((this.state.dynamic[i].question==''|| this.state.dynamic[i].answer==''  ))
      {
        await this.setState({nullValue:true})
        break;
      }


    if(this.state.nullValue==true){
      alert("Fields can't be empty")

    }
    else{
      let id = this.props.match.params.id
    let month = this.props.match.params.month
    console.log(this.state.dynamic, 'state');
    fetch(apiconstants.URL + `qanda_dynamic/${id}/${month}`, {
      method: 'PATCH',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(this.state.dynamic)

    }).then(res=> res)
    .then(res => {
      console.log(res.status)

      })
      this.setState({
        redirect: true
      })

         if (this.state.redirect) {
          return <Redirect to={"/goals" + "/" + (this.props.match.params.mid) + "/" + (this.props.match.params.id) + "/" + (this.props.match.params.month) + "/" + (this.props.match.params.emp) + "/" + (this.props.match.params.pro)} />;

        }

    }

  };
  }

  handleAdd = () => {
    this.setState({
      dynamic: this.state.dynamic.concat({ id: this.props.match.params.id, question: "", answer: "", remark: "", month: this.props.match.params.month })
    });
  };

  setRedirect1 = (event) => {
    event.preventDefault();
    console.log(this.state)
    this.setState({
      redirect1: true
    })
  };

  render() {
    {
      if (this.state.redirect) {
        return <Redirect to={"/goals" + "/" + (this.props.match.params.mid) + "/" + (this.props.match.params.id) + "/" + (this.props.match.params.month) + "/" + (this.props.match.params.emp) + "/" + (this.props.match.params.pro)} />;

      }
    }
    {
      if (this.state.redirect1) {
        return <Redirect to={"/static-form" + "/" + (this.props.match.params.mid) + "/" + (this.props.match.params.id) + "/" + (this.props.match.params.month) + "/" + (this.props.match.params.emp) + "/" + (this.props.match.params.pro)} />;

      }
    }
    var mid = this.props.match.params.mid;
    return (

      <div className="manbg">
        <Managerheader1 mid={mid} />
        <Label size='large' style={{ color: "black", width: "auto" }} ><Icon name='user' />

          {this.props.match.params.pro}
        </Label >
        <br />
        <br />
        <br />

        <h1 style={{ color: " #303030" }}>Employee Feedback-2 :<span style={{color:"darkblue",fontWeight:"bolder"}}>{this.props.match.params.emp}</span> </h1>
        <br />
             <Form >
          {this.state.dynamic.map((row, i) => (
            <div style={{ paddingLeft: "10%" }} className="form1">

              <TextArea style={{ color: "black", height: "60px" }}
                placeholder="Enter your question"
                value={row.question}
                disabled={this.state.disabled}
                onChange={this.handleQuesChange(i)}
             required >{row.question}</TextArea>
              <br />

              <TextArea style={{ color: "black" }}
                placeholder='Enter answer here'
                value={row.answer}
                disabled={this.state.disabled}
                style={{ backgroundColor: bgColors.Yellow }}
                onChange={this.handleAnsChange(i)}
                required >{row.answer}</TextArea>
              <br />

              <TextArea style={{ color: "black", height: "60px" }}
                placeholder='Remarks. If any?'
                style={{ backgroundColor: bgColors.Green }}
                value={row.remark}
                disabled={this.state.disabled}
                onChange={this.handleRemarkChange(i)}
                required  >{row.remark}</TextArea>
            </div>
          ))}
          <br />
          <br />
          <br />

          <button onClick={this.setRedirect1}
            style={{ width: "auto", }}
            className="ui blue button"> <Icon name="arrow circle left" />Go Back</button>

          <Button
            type="button"
            onClick={this.handleAdd}
            className="ui blue button"
            style={{ width: "auto" }} > Add more questions </Button>

          <Button onClick={this.handleSubmit} style={{ width: "auto" }} className="ui green button" >   Save <Icon name="arrow circle right" /></Button>
        </Form>
        <br /><br /><br /><br /><br /><br /><br />
        <ManagerFooter2 />
      </div>
    );
  }
}

export default Managermain4;
