  "Default": "#81b71a",
  "Blue": "#00B1E1",
  "Cyan": "#",
  "Green": "#98FB98",
  "Red": "#E9573F",
  "Yellow": "#d3d3d3",
};


class Managermain4 extends Component {
  constructor() {
    super();

    this.state = {
      // id: 26,
      // month: "aug",
      dynamic: [],
      disabled:false,

    };
    //console.log("ehehe ",this.state.dynamic)
