import React from 'react'
import { Form, TextArea } from 'semantic-ui-react'
import Managerheader1 from "../Managerpg1/Managerheader1";
import ManagerFooter2 from "../Managerpg2/ManagerFooter2";
import ManagerNav from "../Managerpg1/ManagerNav"
import "../Managerpg1/Managermain.css"
import { Redirect, B } from "react-router-dom";
import axios from "axios"
import apiconstants from '../employee/config';
import { async } from 'q';

var bgColors = {
    "Default": "#81b71a",
    "Blue": "#00B1E1",
    "Cyan": "#",
    "Green": "#8CC152",
    "Red": "#E9573F",
    "Yellow": "#d3d3d3",
};

export default class Test extends React.Component {
    constructor(props) {
        super(props);
        this.state = {

            q1: '1. How is everything going today? Tell me about this last week?',
            a1: '',
            q2: '2. What has gone well/not so well for you in this month?',
            a2: '',
            q3: '3. What challenges have you faced?',
            a3: '',
            q4: '4. How are you/your team progressing towards established goals?',
            a4: '',
            q5: '5. What are you committing to between now and the next time we meet?',
            a5: '',
            disabled: false,

        }
    }


    componentWillMount() {
        var id = this.props.match.params.id
        var month = this.props.match.params.month

        console.log(id)
        const response = axios({
            method: 'post',
            // url: 'http://192.168.1.245:8095/updatemonthvalue',
            url: apiconstants.URL + `updatemonthvalue/${id}/${month}`,
            data: this.state
        })
            .then(response => {
                console.log(JSON.stringify(response.data + "  first"))
                if (response.data.length != 0) {
                    console.log(JSON.stringify(response.data.ans))
                    this.setState({ a1: response.data[0].ans });
                    this.setState({ a2: response.data[1].ans });
                    this.setState({ a3: response.data[2].ans });
                    this.setState({ a4: response.data[3].ans });
                    this.setState({ a5: response.data[4].ans });
                }
                else {
                    console.log("second", response)
                    this.setState({ a1: '' });
                    this.setState({ a2: '' });
                    this.setState({ a3: '' });
                    this.setState({ a4: '' });
                    this.setState({ a5: '' });
                }
            })

    }


    handelAnswerchange1 = (event) => {

        this.setState({
            a1: event.target.value

        })
    };


    handelAnswerchange2 = (event) => {
        this.setState({
            a2: event.target.value
        })
    };

    handelAnswerchange3 = (event) => {
        this.setState({
            a3: event.target.value
        })
    };

    handelAnswerchange4 = (event) => {
        this.setState({
            a4: event.target.value
        })
    };

    handelAnswerchange5 = (event) => {
        this.setState({
            a5: event.target.value
        })
    };


    setRedirect = (event) => {
        event.preventDefault();
        console.log(this.state)
        let id = this.props.match.params.id
        let month = this.props.match.params.month

        const response = axios({
            method: 'post',
            // url:'http://192.168.1.245:8095/qanda_static',
            url: apiconstants.URL + `qanda_static/${id}/${month}`,
            data: this.state


        });
        this.setState({
            redirect: true
        })
        //window.location.reload();
    };

    async componentDidMount() {
        console.log("Component Did mount started")
        let val=await this.time();
        console.log("value is "+val)
        if(val===0){
            console.log("it entered the if "+val)
            await setTimeout(()=>{this.setState({disabled:true})},3000);
        }

    }



    //     time = () =>{
    //         console.log(this.state)
    //            var id=this.props.match.params.id
    //         var month=this.props.match.params.month

    //         const response = axios({
    //             method: 'get',
    //             url: apiconstants.URL + `/employeetimestamp/${id}/${month}`,
    //             // data: this.state
    //         }).then(response => {
    //             console.log(response.data.time)
    //             if(response.data.time === 1)
    //             {
    //                 alert ('Cannot Edit')
    //             }
    // })
    //     }



    time = async () => {
        console.log("state is:"+JSON.stringify(this.state));

        // const response = axios({
        //     method: 'get',
        //     url: apiconstants.URL + `/employeetimestamp/${1}/${"jan"}`,
        //     data: this.state
        // }).then(response => {
        //     console.log(response.data.time)
        //     if (response.data.time == 1)
        //         setTimeout(() => {
        //             this.setState({
        //                 disabled: true
        //             })
        //         }, 30000);


        // })
        var id = this.props.match.params.id
        var month = this.props.match.params.month

        const response =await axios({

            method: 'get',
            url: apiconstants.URL + `/employeetimestamp/${id}/${month}`,
            // data: this.state
        })
            console.log(response.data.time)
            return await response.data.time;
    }




    render() {


        // var url_string = window.location.href
        // var url = new URL(url_string);
        // var c = url.searchParams.get("c");

        // console.log(window.location.href);
        // console.log(c)


        {
            let id = this.props.match.params.id
            let month = this.props.match.params.month
            if (this.state.redirect) {
                return <Redirect to={"/dynamic-form" + "/" + (this.props.match.params.id) + "/" + (this.props.match.params.month) + "/" + (this.props.match.params.emp)} />;

            }
        }


        return (
            <div className="manbg">
                <Managerheader1 />
                <ManagerNav />
                <br />
                <br />




                <h1 className="ui" style={{ color: "black" }}> your employee '{this.props.match.params.emp}'</h1>
                <Form>
                    <input value={this.state.q1} />
                    <TextArea style={{ backgroundColor: bgColors.Yellow }}
                        placeholder='Answers'
                        value={this.state.a1}
                        disabled={this.state.disabled}
                        onChange={this.handelAnswerchange1}

                    />
                </Form>


                <Form>
                    <input value={this.state.q2} />
                    <TextArea style={{ backgroundColor: bgColors.Yellow }}
                        placeholder='Answers'
                        value={this.state.a2}
                        onChange={this.handelAnswerchange2}
                    />
                </Form>


                <Form>
                    <input value={this.state.q3} />
                    <TextArea style={{ backgroundColor: bgColors.Yellow }}
                        placeholder='Answers'
                        value={this.state.a3}
                        onChange={this.handelAnswerchange3} />
                </Form>


                <Form>
                    <input value={this.state.q4} />
                    <TextArea style={{ backgroundColor: bgColors.Yellow }}
                        placeholder='Answers'
                        value={this.state.a4}
                        onChange={this.handelAnswerchange4} />
                </Form>



                <Form>
                    <input value={this.state.q5} />
                    <TextArea style={{ backgroundColor: bgColors.Yellow }}
                        placeholder='Answers'
                        value={this.state.a5}
                        onChange={this.handelAnswerchange5} />
                </Form>
                <br />
                <br />
                <br />
                <br />


                <button onClick={this.setRedirect}
                    style={{ "width": "200px", margin: "60px" }}
                    className="ui positive button">Save & Next</button>
                <br />
                <br />
                <br />
                <br />


            </div>
        )
    }

}
