import React,{Component} from 'react';
import { Form, TextArea,Button,Segment } from 'semantic-ui-react';
import Managerheader1 from "../Managerpg1/Managerheader1";
import ManagerFooter2 from "../Managerpg2/ManagerFooter2";
import './Managermain4.css';
import axios from 'axios';
import {Redirect} from "react-router-dom";

class Managermain4 extends Component {
    constructor() {
      super();

      this.state = {

        //   dynamic: [{id:3, q:'',a:'',remark:'' }],

       id:1,
       month:"jan",
        dynamic: [],
        form:[]
