import React from 'react'
import './signupMainbody.css'
import SignupHeader from './signupHeader'
import { Redirect } from 'react-router-dom'
 import apiconstants from '../employee/'
import axios from 'axios';


export default class SignupMainbody extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            username: "",
            email: "",
            password: "",
            confirmPassword: "",
            phone: "",

        }
    }
    handleChange = e => {
        this.setState({
            [e.target.name]: e.target.value
        });
    }
    submitUserRegistrationForm = async (e) => {
        e.preventDefault();
        console.log(this.state);
        const response = await axios({
            method: 'post',
            url: apiconstants.URL + 'employee',
            headers: {
                'Content-Type': 'application/json',
            },

            data: this.state
        });
        console.log(response);
        let a=true;

        if(this.state.password !== this.state.confirmPassword )
            {
                alert("Password don't match")
                let a=false;
            }


        if (response.data==="successfull")
        {


                this.props.history.push('/login')


        }
        else if(response.data==="existing")
        {
            alert('Credentials already exists!!!!')
        }
        else
        {
            alert('Fill the form correctly!!!')
        }


    };
    render() {
        const { username, email, password, confirmPassword, phone } = this.state
        return (

            <div>
                <SignupHeader />
                <div className="firstbg">

                    <form onSubmit={this.submitUserRegistrationForm}>
                        <h1 className='head'>ONE-ON-ONE</h1>
                        <div className="container">
                            <div className="card card-container">

                                <p id="profile-name" className="profile-name-card" />
                                <form name="userRegistrationForm" className="form-signin" >
                                    <h1 className="log">SIGN UP</h1>


                                    <input
                                        //className="{formErrors.username.length > 0 ? "error" : null}"
                                        type="text"
                                        id="inputUsername"
                                        name="username"
                                        className="form-control"
                                        placeholder="UserName"
                                        value={this.state.username}
                                        onChange={this.handleChange}
                                        required autoFocus />

                                    <input type="email"
                                        id="inputEmail"
                                        name="email"
                                        className="form-control"
                                        placeholder="Email"
                                        value={this.state.email}
                                        onChange={this.handleChange}

                                        required />



                                    <input type="password"
                                        id="inputPassword"
                                        name="password"
                                        className="form-control"
                                        placeholder="Password"
                                        value={this.state.password}
                                        onChange={this.handleChange}
                                        //onChange={(e) => this.handleChange(e)}
                                        required />

                                    <input type="password"
                                        id="inputConfirmPassword"
                                        name="confirmPassword"
                                        className="form-control"
                                        placeholder="Confirm Password"
                                        value={this.state.confirmPassword}
                                        onChange={this.handleChange}
                                        //onChange={(e) => this.handleChange(e)}
                                        required />



                                    <input type="text"
                                        name="phone"

                                        id="inputMobileNum"
                                        className="form-control"
                                        placeholder="Phone"
                                        value={this.state.phone}
                                        onChange={this.handleChange}


                                        required />


                                    <br />
                                    <button type="button" className="btn btn-lg btn-primary btn-block btn-signin"
                                        onClick={this.submitUserRegistrationForm}
                                        type="submit">
                                        Sign up
                            </button>

                                </form>

                            </div>
                            {/* /card-container */}
                        </div>
                    </form>
                </div>
            </div>


        );
    }

}

//export default signupMainbody;
