import React from 'react'
import 'semantic-ui/dist/semantic.min.css';
import Header from "../Components/Header";
import Tab from "../Components/Tab"
import Footer from "../Components/Footer";
import axios from 'axios'
import { Form, TextArea, Button, Segment, Icon, Label } from 'semantic-ui-react';
import { Redirect } from "react-router-dom";
import apiconstants from '../employee/config';
import Pop from './Pop';
import Popup1 from '../Components/popup1'

var bgColors = {
  "Yellow": "#d3d3d3",
};

class Employeepage1 extends React.Component {

  constructor() {
    super();
    this.state = {
      dis: '',
      button: true,
      form: [],
      showPopup: false
    };
  }
  togglePopup() {
    console.log('toggled');
    this.setState({
      showPopup: !this.state.showPopup
    });
    console.log('toggled and the state is ' + this.state.showPopup);
  }

  componentWillMount() {
    let id = this.props.match.params.id
    let month = this.props.match.params.month
    const response = axios({
      method: 'POST',
      url: apiconstants.URL + `prepopulate/${11}/${9}/${2019}`,

    })
      .then(res => {
        if (res.data.length != 0) {

          this.setState({ form: res.data })
        }
        else {
          alert(' feedback is not been recorded ')
          this.setState({ form: [] });
        }
        console.log(res.data)
      })
  }
  setRedirect = (event) => {
    event.preventDefault();
    console.log(this.state)
    this.setState({
      redirect: true
    })
  };

  setRedirect1 = (event) => {
    event.preventDefault();
    console.log(this.state)
    this.setState({
      redirect1: true
    })
  };

  // handelDisagreeform = (event) => {
  //   this.setState({
  //     dis: event.target.value
  //   })
  // };



  // button = () => {
  //   this.setState({ showPopup: !this.state.showPopup })
  // }




  render() {

    //     {
    //       if (this.state.redirect) {
    //           return <Redirect to={"/em-goals"+ "/" +this.props.match.params.id+ "/" +this.props.match.params.emp + "/" +this.props.match.params.month }/>;

    //       }
    //   }
    //   {
    //     if (this.state.redirect1) {
    //         return <Redirect to={"/em-month"+"/"+ this.props.match.params.id+"/"+ this.props.match.params.emp}/>;

    //     }
    // }
    return (

      <div className="manbg">
        <Header idd={this.props.match.params.id} />

        <Tab mid={this.props.match.params.mid}
          id={this.props.match.params.id}
          month={this.props.match.params.month}
          emp={this.props.match.params.emp}
          pro={this.props.match.params.pro}
        // pathURL={pathURL}
        />

        <Label size='large' style={{ color: "black", width: "auto" }} ><Icon name='user' />
          {this.props.match.params.emp}
        </Label>

        {this.state.form.map(el => (
          <Form>
            <input value={el.qno + "."+  el.question} />
            <TextArea style={{ backgroundColor: bgColors.Yellow }}
              value={el.answer}
              disabled={true}
            />
          </Form>
        ))}

        <button
          style={{ width: "auto", }}
          onClick={()=>this.setState({agree:true})}
          className="ui green button">  I Agree</button>
           {
          this.state.agree === true &&
          <div>
            <Popup1
              modal=''
              text='Reason to agree'
              closePopup={this.togglePopup.bind(this)}
            />
          </div>
        }


        <button onClick={this.togglePopup.bind(this)}
          style={{ width: "auto", }}
          className="ui red button">Disagree</button>
        {
          this.state.showPopup === true &&
          <div>
            <Pop
              text='Reason for disagree'
              closePopup={this.togglePopup.bind(this)}
            />
          </div>

        }
        <Footer />

      </div>
    );
  }
}

export default Employeepage1
