import React from 'react'
import './logMainbody.css'
import axios from 'axios';
import LogHeader from './logHeader'
import { Redirect } from "react-router-dom";
//import apiconstants from '../employee/config'

class LogMainbody extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            email: '',
            password: ''
        }

    }

    changeHandler = e => {
        this.setState({
            [e.target.name]: e.target.value
        });
    }


    submitHandler = async (e) => {
        e.preventDefault();
        console.log(this.state);

        const response = await axios({
            method: 'post',
            url: 'http://192.168.1.245:8095/login',
            data: this.state
        })

        console.log(response)

        if (response.data.status==="true") {



                    if(response.data.designation === "manager")
                    {
                        this.props.history.push('/manager')
                    }
                    else if(response.data.designation === "employee")
                    {
                        this.props.history.push('/e')
                    }
                    else{
                        this.props.history.push('/hr')
                    }
                    return (
                        console.log(JSON.stringify(response.data)),
                        localStorage.setItem("id", response.data.id),
                        localStorage.setItem("empname", response.data.empname),
                        localStorage.setItem("phone", response.data.phone),
                        localStorage.setItem("email", response.data.email),
                        localStorage.setItem("designation", response.data.designation));}

        else {
            alert("Wrong Credentials")
        }



};




render() {
    const { email, password } = this.state;

    if (this.state.redirect) {
        return <Redirect to="/manager" />;
    }
    return (
        <div className="firstbg">
            <div className="form-signin">
                <LogHeader />
                <form>

                    <h1 className='head'>ONE-ON-ONE</h1>
                    <div className="container">
                        <div className="card card-container">
                            <h1 className="log">LOGIN</h1>
                            <input
                                type="email"
                                id="email"
                                name="email"
                                className="form-control"
                                placeholder="Email ID"
                                value={email} onChange={this.changeHandler}
                            />
                            <input
                                type="password"
                                id="password"
                                name="password"
                                className="form-control"
                                placeholder="Password"
                                value={password} onChange={this.changeHandler}
                            />
                            <button
                                className="btn btn-lg btn-primary btn-block btn-signin" onClick={this.submitHandler}
                            >Sign in</button>

                        </div>{/* /card-container */}

                    </div>
                </form>
            </div>
            <br />
            <br />
            <br />
            <br />
            <br />
            <br />
        </div>

    );
}

}
export default LogMainbody
