import 'semantic-ui/dist/semantic.min.css';
import Empheader from './empheader'
import '../Managerpg2/Managermain2.css'
import ManagerFooter from "../Managerpg1/ManagerFooter";
import axios from 'axios'
import { Form, TextArea,Button,Segment,Icon,Label } from 'semantic-ui-react';
import {Redirect} from "react-router-dom";
import apiconstants from '../employee/config';
import { thisExpression } from '@babel/types';
var bgColors = {
    "Yellow": "#d3d3d3",
};
class Empmain3 extends React.Component {

        constructor() {
          super();
          this.state = {
            form: [] };
        }

        componentWillMount() {
          let id=this.props.match.params.id
          let month=this.props.match.params.month
          const response = axios({
              method: 'POST',
              url: apiconstants.URL+`updatemonthvalue/${id}/${month}`,
              // data: this.state

          })
          .then(res =>{
            if(res.data.length!=0){

              this.setState({form:res.data})
            }
else{

  this.setState({form:[]});
}
console.log(res.data)      
        })
      }








    setRedirect = (event) => {
      event.preventDefault();
     console.log(this.state)
      this.setState({
          redirect: true
      })
      //window.location.reload();
  };

  setRedirect1 = (event) => {
    event.preventDefault();
   console.log(this.state)
    this.setState({
        redirect1: true
    })
    //window.location.reload();
};



        render() {

          {
            if (this.state.redirect) {
                return <Redirect to={"/em-goals"+ "/" +this.props.match.params.id+ "/" +this.props.match.params.emp + "/" +this.props.match.params.month }/>;

            }
        }
        {
          if (this.state.redirect1) {
              return <Redirect to={"/em-month"+"/"+ this.props.match.params.id+"/"+ this.props.match.params.emp}/>;

          }
      }
            // localStorage.setItem("a1",'tttt')
          return (

            <div className="manbg">
          <Empheader idd={this.props.match.params.id} />
              <Label size='large' style={{color:"black",width :"auto"}} ><Icon name='user' />

      {this.props.match.params.emp}
    </Label> 

                {this.state.form.map(el => (
                      <Form>
                        <input value= {el.qno+"."+el.question}/>
                        <TextArea style={{backgroundColor: bgColors.Yellow}}
                                  value=  {el.answer}

                        />
                    </Form>
                ))}

<button onClick={this.setRedirect1}
                            style={{ width :"auto",}}
                            className="ui blue button"> <Icon name="arrow circle left" /> Go Back</button>



<button onClick={this.setRedirect}
                            style={{ width :"auto", }}
                            className="ui positive button">View Goals</button>


                <br/>
                <br/>
                <br/>
                <ManagerFooter/>

            </div>
          );
        }
      }

export default Empmain3
